import React, { useEffect, useCallback, useState, useMemo } from "react";

import { getAllMockImages } from "../../services/image-service";

const Carousel = () => {
  let currentIndex = 0;
  const [imagesData, setImagesData] = useState();

  const getImages = useCallback(async () => {
    const data = await getAllMockImages();

    setImagesData(data);
  }, []);

  useEffect(() => {
    getImages();
  }, [getImages]);

  useEffect(() => {
    document
      .getElementById("images-wrapper")
      .addEventListener("wheel", (event) => {
        console.log("event", event);
        if (event.deltaY > 0) {
          currentIndex++;
          console.log(imagesData?.length);
        } else if (currentIndex > 0) {
          currentIndex--;
        }
        const imageIndex =
          document.getElementsByTagName("img")?.[currentIndex]?.offsetTop;

        document.getElementById("images-wrapper").scrollTo({
          top: imageIndex,
          behavior: "smooth",
        });
      });

    return () => {
      document
        .getElementById("images-wrapper")
        ?.removeEventListener("scroll", (event) => {});
    };
  }, [currentIndex]);

  return (
    <div id="images-wrapper">
      {imagesData &&
        imagesData?.map((elm) => {
          return <img src={elm?.download_url} alt={elm?.author} />;
        })}
    </div>
  );
};

export default Carousel;
