import React, { useEffect, useCallback, useState, useMemo } from "react";

import { getAllMockImages } from "../../services/image-service";

const Carousel = () => {
  const imagesWrapper = useMemo(
    () => document.getElementById("images-wrapper"),
    []
  );
  const [imagesData, setImagesData] = useState();
  let currentIndex = 0;
  const imageHeight = (window.innerWidth / 16) * 9 - 10;

  const getImages = useCallback(async () => {
    const data = await getAllMockImages();

    setImagesData(data);
  }, []);

  useEffect(() => {
    getImages();
  }, [getImages]);

  useEffect(() => {
    if (imagesWrapper) {
      imagesWrapper.addEventListener("wheel", (event) => {
        console.log("event", event);
        if (event.deltaY > 0) {
          currentIndex++;
        } else {
          currentIndex--;
          // setCurrentIndex((old) => (old -= 1));
        }

        console.log("currentIndex * imageHeight", currentIndex * imageHeight);

        imagesWrapper.scrollTo({
          top: currentIndex * imageHeight,
          behavior: "smooth",
        });
      });
    }

    return () => {
      imagesWrapper?.removeEventListener("scroll", (event) => {});
    };
  }, [imagesWrapper, currentIndex, imageHeight]);

  return (
    <div id="images-wrapper">
      {imagesData &&
        imagesData?.map((elm) => {
          return <img src={elm?.download_url} alt={elm?.author} />;
        })}
    </div>
  );
};

export default Carousel;
