import React, { useEffect, useCallback, useState, useRef } from "react";

import { getAllMockImages } from "../../services/image-service";

const Carousel = () => {
  const imagesWrapperRef = useRef(null);

  const [page, setPage] = useState(1);
  const [imgIndex, setImgIndex] = useState(0);
  const [imagesData, setImagesData] = useState();
  const [activeImage, setActiveImage] = useState();
  console.log("imagesData", imagesData);

  const imagesWrapper = imagesWrapperRef.current;

  const getImages = useCallback(async () => {
    const data = await getAllMockImages(page);

    if (page === 1) {
      setActiveImage(data?.[0]);
    }

    setImagesData((old) => {
      if (old?.length) {
        return [...old, ...data];
      } else {
        return data;
      }
    });
  }, [page]);

  useEffect(() => {
    if (imagesWrapper) {
      // const imageElement = imagesWrapper.querySelectorAll("img")[imgIndex];
      // const imageRect = imageElement.getBoundingClientRect();

      if (imgIndex % 10 === 5 && imgIndex > page * 10 + 6) {
        console.log("imgIndex", imgIndex);
        console.log("%", imgIndex % 10);

        setPage((old) => (old += 1));
      }

      setActiveImage(imagesData?.[imgIndex]);

      // imagesWrapper.scrollTo({
      //   top: imageRect?.top + imagesWrapper.scrollTop,
      //   behavior: "smooth",
      // });
    }
  }, [imgIndex, page, imagesData, imagesWrapper]);

  useEffect(() => {
    getImages();
  }, [getImages]);

  const handleWheel = (event) => {
    if (imgIndex + 1 === imagesData?.length) {
      setImgIndex(0);
    } else if (event.deltaY > 0) {
      setImgIndex((old) => (old += 1));
    } else if (imgIndex > 0) {
      setImgIndex((old) => (old -= 1));
    }
  };

  return (
    <>
      <div id="images-wrapper" ref={imagesWrapperRef} onWheel={handleWheel}>
        <img
          key={activeImage?.id}
          alt={activeImage?.links?.slug}
          src={activeImage?.links?.download}
        />
        {/* {imagesData &&
          imagesData.map((elm) => {
            return (
              <img
                key={elm?.id}
                alt={elm?.links?.slug}
                src={elm?.links?.download}
              />
            );
          })} */}
      </div>
    </>
  );
};

export default Carousel;
