import React, { useEffect, useCallback, useState, useRef } from "react";
import { getAllMockImages } from "../../services/image-service";

const Carousel = () => {
  let index = 0;
  const [imagesData, setImagesData] = useState();
  const imagesWrapperRef = useRef(null);

  const getImages = useCallback(async () => {
    const data = await getAllMockImages();
    setImagesData(data);
  }, []);

  useEffect(() => {
    getImages();
  }, [getImages]);

  useEffect(() => {
    const imagesWrapper = imagesWrapperRef.current;

    const observerOptions = {
      root: null,
      rootMargin: "0px",
      threshold: 0.1, // Adjust the threshold as per your requirement
    };

    const handleIntersection = (entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          const img = entry.target;
          const src = img.getAttribute("data-src");
          img.setAttribute("src", src);
          observer.unobserve(img);
        }
      });
    };

    const observer = new IntersectionObserver(
      handleIntersection,
      observerOptions
    );

    const images = imagesWrapper.querySelectorAll("img[data-src]");
    images.forEach((img) => {
      observer.observe(img);
    });

    return () => {
      images.forEach((img) => {
        observer.unobserve(img);
      });
    };
  }, [imagesData]);

  const handleWheel = (event) => {
    if (event.deltaY > 0) {
      index++;

      if (index === imagesData?.length) {
        index = 0;
      }
    } else if (index > 0) {
      index--;
    }
    
    const imageElement = imagesWrapper.querySelectorAll("img")[index];
    imageElement.scrollIntoView({ behavior: "smooth", block: "nearest" });
  };

  // magesWrapper.addEventListener("wheel", (event) => {
  //   if (event.deltaY > 0) {
  //     index++;

  //     if (index === imagesData?.length) {
  //       index = 0;
  //     }
  //   } else if (index > 0) {
  //     index--;
  //   }
  //   const imageIndex =
  //     document.getElementsByTagName("img")?.[index]?.offsetTop;

  //   console.log("imageIndex", imageIndex);
  //   document.getElementById("images-wrapper").scrollTo({
  //     top: imageIndex,
  //     behavior: "smooth",
  //   });
  // });

  return (
    <>
      <div id="images-wrapper" ref={imagesWrapperRef} onWheel={handleWheel}>
        {imagesData &&
          imagesData.map((elm) => {
            return (
              <img
                src=""
                alt={elm?.links?.slug}
                data-src={elm?.links?.download}
                key={elm?.id}
              />
            );
          })}
      </div>
    </>
  );
};

export default Carousel;
