import React, { useEffect, useCallback, useState, useMemo } from "react";

import { getAllMockImages } from "../../services/image-service";

const Carousel = () => {
  let index = 0;
  const [imagesData, setImagesData] = useState();
  const [currentIndex, setCurrentIndex] = useState(0);

  const getImages = useCallback(async () => {
    const data = await getAllMockImages();

    setImagesData(data);
  }, []);

  useEffect(() => {
    getImages();
  }, [getImages]);

  useEffect(() => {
    const imagesWrapper = document.getElementById("images-wrapper");
    imagesWrapper
      .addEventListener("wheel", (event) => {
        console.log("event", event?.deltaY);
        console.log("currentIndex", currentIndex);
        if (event.deltaY > 0) {
          setCurrentIndex(index + 1);
          index++;

          if (index === imagesData?.length) {
            index = 0;
            setCurrentIndex(0);
          }
        } else if (index > 0) {
          setCurrentIndex(index - 1);
          index--;
        }
        const imageIndex =
          document.getElementsByTagName("img")?.[index]?.offsetTop;

        document.getElementById("images-wrapper").scrollTo({
          top: imageIndex,
          behavior: "smooth",
        });
        console.log("index", index);
      });

    return () => {
      document
        .getElementById("images-wrapper")
        ?.removeEventListener("wheel", (event) => {});
    };
  }, []);

  return (
    <>
      <div id="images-wrapper">
        {imagesData &&
          imagesData?.map((elm) => {
            return <img src={elm?.download_url} alt={elm?.author} />;
          })}
      </div>
    </>
  );
};

export default Carousel;
