import React, { useEffect, useCallback, useState, useRef } from "react";
import { getAllMockImages } from "../../services/image-service";

const Carousel = () => {
  // let index = 0;
  const imagesWrapperRef = useRef(null);
  const [imgIndex, setImgIndex] = useState(1);
  const [imagesData, setImagesData] = useState();

  const getImages = useCallback(async () => {
    const data = await getAllMockImages();
    setImagesData(data);
  }, []);

  useEffect(() => {
    console.log("imgIndex", imgIndex);
  }, [imgIndex]);

  useEffect(() => {
    getImages();
  }, [getImages]);
  const imagesWrapper = imagesWrapperRef.current;

  useEffect(() => {
    const imagesWrapper = imagesWrapperRef.current;

    const handleIntersection = (entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          const img = entry.target;
          const src = img.getAttribute("data-src");
          img.setAttribute("src", src);
          observer.unobserve(img);
        }
      });
    };

    const observer = new IntersectionObserver(handleIntersection);

    const images = imagesWrapper.querySelectorAll("img[data-src]");
    images.forEach((img) => {
      observer.observe(img);
    });

    return () => {
      images.forEach((img) => {
        observer.unobserve(img);
      });
    };
  }, [imagesData]);

  const handleWheel = (event) => {
    console.log("imgIndex", imgIndex);
    if (event.deltaY > 0) {
      // index++;
      setImgIndex((old) => (old += 1));

      if (imgIndex === imagesData?.length) {
        // index = 0;
        setImgIndex(0);
      }
    } else if (imgIndex > 0) {
      // index--;
      setImgIndex((old) => (old -= 1));
    }

    const imageElement = imagesWrapper.querySelectorAll("img")[imgIndex];
    const imageRect = imageElement?.getBoundingClientRect();

    imagesWrapper.scrollTo({
      top: imageRect?.top + imagesWrapper?.scrollTop,
      behavior: "smooth",
    });
  };

  return (
    <>
      <div id="images-wrapper" ref={imagesWrapperRef} onWheel={handleWheel}>
        {imagesData &&
          imagesData.map((elm) => {
            return (
              <img
                src=""
                alt={elm?.links?.slug}
                data-src={elm?.links?.download}
                key={elm?.id}
              />
            );
          })}
      </div>
    </>
  );
};

export default Carousel;
